<?phpadd_action('the_content', 'cp_add_featured');function cp_add_featured($content) {if(!is_singular('coupon')) return $content; ob_start();the_post_thumbnail();$data = ob_get_contents();ob_end_clean();$content = $data.$content;return $content;}/*Добавляем после контента данные*/add_action('the_content', 'add_metacoupon');function add_metacoupon($content) {if(!is_singular('coupon')) return $content; global $post;$data = array();ob_start();?><section class="coupon_section">	<h2>Инструкция по использованию</h2>	<span>	<?php echo get_post_meta($post->ID, 'instruction', true); ?>	</span></section><section class="coupon_section">	<h2>Подробности</h2>	<ul>		<li>Срок: 		<?php echo date("d.m.Y", strtotime(get_post_meta($post->ID, 'finish_at', true))); ?>		</li>		<?php 			$data['start_at'] = get_post_meta($post->ID, 'start_at', true);			if($data['start_at'] != '') echo "<li>Дата начала: ". date("d.m.Y", strtotime(get_post_meta($post->ID, 'start_at', true))) ."</li>";					?>		<li>Тип купона: 		<?php echo get_post_meta($post->ID, 'kind', true); ?>		</li>		<li>Код купона: 		<?php $code = get_post_meta($post->ID, 'promocode', true);				if($code != '') echo $code; else echo 'Не требуется';		?>		</li>	</ul></section><section class="coupon_section">	<a class="button" href="?url=go">Перейти на страницу акции</a></section><?php$data = ob_get_contents();ob_end_clean();$content = $content . $data;return $content;}?>